import componentsImage from './assets/images/components.png';
import stateImage from './assets/images/state.png';
import eventsImage from './assets/images/events.png';

import Card from './Card';
import Header from './components/Header/Header'
import './index.css'


const concepts = [
  {
    title: 'Components',
    image: componentsImage,
    description:
      'Components let you split the UI into independent, reusable pieces, and think about each piece in isolation. Components can receive data via props, and they can render dynamic output using JSX.',
  },
  {
    title: 'State',
    image: stateImage,
    description:
      'State is data that may change over time. As it changes, the UI should be updated to reflect the updated data. Each component can maintain its own state and multiple components can share state.',
  },
  {
    title: 'Events',
    image: eventsImage,
    description:
      'Event handlers are added via props to (built-in) components. You pass functions as values to such event handlers to control which functions gets executed for which event.',
  },
];

function App() {
  return (
    <div>
      <Header></Header>
      <ul id="concepts">
        <Card title={concepts[0].title} imagesrc={concepts[0].image}>
          {concepts[0].description}
        </Card>
        <Card title={concepts[1].title} imagesrc={concepts[1].image}>
          {concepts[1].description}
        </Card>
        <Card title={concepts[2].title} imagesrc={concepts[2].image}>
          {concepts[2].description}
        </Card>
      </ul>
    </div>
  );
}

export default App;
